import React from 'react';
import { Link } from 'react-router-dom';
import { Card, CardImg, CardText, CardBody, CardTitle, Row, Col, Breadcrumb, BreadcrumbItem } from 'reactstrap';

function RenderItem({event}) {
    if(event != null) {
        return(
            <Row>
                <Col sm="12">
                    <Card>
                        <CardImg src={event.image} width="100%" height="600px" />
                        <CardBody>
                            <CardTitle>{event.name}</CardTitle>
                            <CardText>{event.description}</CardText>
                        </CardBody>
                    </Card>
                </Col>
            </Row>
        );
    } else {
        return (
            <div></div>
        );
    }
}

const ItemDetail = (props) => {
    if (props.event != null) {
        return (
            <div className="container">
                <div className="row">
                    <Breadcrumb>
                        <BreadcrumbItem>
                            <Link to='/gallery'>Gallery</Link>
                        </BreadcrumbItem>
                        <BreadcrumbItem active>
                            {props.event.name}
                        </BreadcrumbItem>
                    </Breadcrumb>
                    <div className="col-12">
                        <h3>{props.event.name}</h3>
                        <hr />
                    </div>
                </div>
                <div className="row">
                    <div className="col-12 m-1">
                        <RenderItem event={props.event} />
                    </div>
                </div>
            </div>
        );
    } else {
        return (
            <div></div>
        );
    }
};

export default ItemDetail;
